const cron = require('node-cron');

let items = [
    { name: 'A', cronPattern: '* * * * * *'},
    { name: 'B', cronPattern: '*/2 * * * * *'},
    { name: 'C', cronPattern: '*/5 * * * * *'}
];

items.forEach((item) => {
    cron.schedule(item.cronPattern, () => {
        console.log(new Date(), item);
    });
});